/*==================================================
CampusContainer.js

The Container component is responsible for stateful logic and data fetching, and
passes data (if any) as props to the corresponding View component.
If needed, it also defines the component's "connect" function.
================================================== */
import Header from './Header';
import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchCampusThunk, deleteCampusThunk, editCampusThunk } from "../../store/thunks";

import { CampusView } from "../views";
import { deleteCampus, editCampus } from '../../store/actions/actionCreators';

class CampusContainer extends Component {
    constructor(props){
        super(props);
        this.state = {
            name: props.campus.name, 
            address: props.campus.address, 
            description: props.campus.description, 
            imageUrl: props.campus.imageUrl,
            id: props.campus.id,
            students: props.students,
            redirect: false, 
            redirectId: null
          };
      }

  // Get the specific campus data from back-end database
  componentDidMount() {
    // Get campus ID from URL (API link)
    this.props.fetchCampus(this.props.match.params.id);
  }

  removeStudent (id) {
    // event.preventDefault();  // Prevent browser reload/refresh after submit.

    // Edit student in back-end database
    this.props.editCampus(this.props.campus.id, this.props.removeStudentCampus(id));

    // Update state, and trigger redirect to show the edited student
    this.setState({
        name: this.props.campus.name, 
        address: this.props.campus.address, 
        description: this.props.campus.description, 
        imageUrl: this.props.campus.imageUrl,
        students: this.props.campus.students,
        redirect: true, 
        redirectId: this.props.campus.id
    });

  }


  removeStudentCampus = id => {
    var updatedCampus = this.props.campus;
    updatedCampus.students.map((student )=> {
        if(student.id === id){ 
            return student.campusId = null;
        }});
    console.log(updatedCampus);
    return updatedCampus;
}
componentWillUnmount() {
    this.setState({redirect: false, redirectId: null});
}



  // Render a Campus view by passing campus data as props to the corresponding View component
  render() {
    return (
      <div>
        <Header />
        <CampusView 
        campus={this.props.campus} 
        deleteCampus={this.props.deleteCampus}  
        editCampus={this.props.editCampus} 
        removeStudent={this.props.removeStudent}
        />
      </div>
    );
  }
}

// The following 2 input arguments are passed to the "connect" function used by "CampusContainer" component to connect to Redux Store.
// 1. The "mapState" argument specifies the data from Redux Store that the component needs.
// The "mapState" is called when the Store State changes, and it returns a data object of "campus".
const mapState = (state) => {
  return {
    campus: state.campus,  // Get the State object from Reducer "campus"
  };
};
// 2. The "mapDispatch" argument is used to dispatch Action (Redux Thunk) to Redux Store.
// The "mapDispatch" calls the specific Thunk to dispatch its action. The "dispatch" is a function of Redux Store.
const mapDispatch = (dispatch) => {
  return {
    fetchCampus: (id) => dispatch(fetchCampusThunk(id)),
    deleteCampus: (campusId) => dispatch(deleteCampusThunk(campusId)),
    editCampus: (campusId, campus) => dispatch(editCampusThunk(campusId, campus)),
  };
};

// Export store-connected container by default
// CampusContainer uses "connect" function to connect to Redux Store and to read values from the Store 
// (and re-read the values when the Store State updates).
export default connect(mapState, mapDispatch)(CampusContainer);
