/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */
import { Link } from "react-router-dom";

// Take in props data to construct the component
const CampusView = (props) => {
  const {campus, deleteCampus, editCampus} = props;
    // If there is no campus, display a message.

    function removeStudentCampus(id) {
        var updatedCampus = campus;
        updatedCampus.students.map((student )=> {
            if(student.id === id){ 
                return student.campusId = null;
            }});
        console.log(updatedCampus);
        return updatedCampus;
    }

     function removeStudent(id) {
        // event.preventDefault();  // Prevent browser reload/refresh after submit.
        console.log(id);
        // Edit student in back-end database
        editCampus(campus.id, removeStudentCampus(id));

        // Update state, and trigger redirect to show the edited student
        this.setState({
            name: this.props.campus.name, 
            address: this.props.campus.address, 
            description: this.props.campus.description, 
            imageUrl: this.props.campus.imageUrl,
            students: this.props.campus.students,
            redirect: true, 
            redirectId: this.props.campus.id
        });

      }


  function getStudents(campus) {
    if (!campus.students.length) {
        return <div>There are no students.</div>;
      }
      else{
        return campus.students.map( student => {
            let name = student.firstname + " " + student.lastname;
            return (
              <div key={student.id}>
                <h2>
                    <Link to={`/student/${student.id}`}>
                    {name}
                    </Link>          
                    <button onClick={() => removeStudent(student.id)}>Delete Student</button>   
                </h2>
              </div>
            );
          })
      }
    }

  // Render a single Campus view with list of its students
  return (
    <div>
      <img src={campus.imageUrl}></img>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>
      {getStudents(campus)}
      <br/>
      <br/>
    <Link to={`/editcampus/${campus.id}`}>
        <button>Edit Campus</button>
    </Link>
    <br/><br/>
    <Link to={`/campuses/`}>
    <button onClick={() => deleteCampus(campus.id)}>Delete</button>
    </Link>
    </div>
  );
};

export default CampusView;
